mapper.select:
SELECT clpr FROM kospi_stock_list
WHERE srtnCd = #{srtnCd}
  AND basDt = #{date}

SELECT total_price FROM trade
WHERE srtnCd = #{srtnCd}
  AND basDt = #{date}
  AND user_id = #{userId}

SELECT t.stock_code, t.stock_name, t.total_price, t.total_stock_holdings
FROM trade t
         INNER JOIN (
    SELECT stock_name, MAX(created_at) as latest
    FROM trade
    WHERE user_id = #{VALUE}
    GROUP BY stock_name
) latest_trade
                    ON t.stock_name = latest_trade.stock_name AND t.created_at = latest_trade.latest
WHERE t.user_id = #{VALUE}
